mapper.select:
select count(*) from board where status = 'Y'
and board_lang = #{lang}
and answer = #{answer}

select board_no , code , board_title , board_content , board_count , to_char(create_date, 'YYYY-MM-DD') as "create_date" , answer , board_lang from board where status = 'Y' and board_writer = #{memNo}
and board_lang = #{lang}
and answer = #{answer}
order by board_no desc

mapper.insert:
insert
  into board
  	 (
  	   board_no
  	 , board_writer
  	 , code
  	 , board_title
  	 , board_content
  	 , board_lang
  	 )
values
     (
       seq_bno.nextval
     , #{boardWriter}
     , #{code}
     , #{boardTitle}
     , #{boardContent}
     , #{boardLang}
     )

mapper.update:
update board
   set board_count = board_count + 1
 where board_no = #{bNo}
