mapper.insert:


    	SELECT concat('forum-', seq_rno.currval) FROM DUAL


	insert 
	  into room
	     (
	       room_id
	     , room_name
	     , room_pwd
	     , language
	     )
	values
	     (
	       concat('forum-', seq_rno.nextval)
	     , #{roomName}
	     , #{roomPwd}
	     , #{language}
	     )
